function startApp(){sendTransaction(),renderChart()}function sendTransaction(){for(var e=document.querySelectorAll(".send"),t=0;t<e.length;t++)e[t].addEventListener("click",function(){web3.eth.sendTransaction({from:web3.eth.defaultAccount,to:this.dataset.address,value:0},function(e,t){e||console.log("sendTransaction",t)})})}function renderChart(){var e=document.getElementById("pie-chart"),t=(new Chart(e,{type:"doughnut",data:{labels:["YES","NO"],datasets:[{data:[300,50],backgroundColor:["#36A2EB","#FFCE56"]}]},options:{responsive:!0,legend:{display:!1,position:"bottom"}}}),document.getElementById("bar-chart"));new Chart(t,{type:"horizontalBar",data:{labels:["NO","YES: 0 ≤ reward < 1.5","YES: 1.5 ≤ reward < 2","YES: 2 ≤ reward < 3","YES: 3 ≤ reward < 4","YES: reward ≥ 4"],datasets:[{data:[9437,3698.8106,619.9956,322,2121,32121],backgroundColor:["#FFCE56","#36A2EB","#36A2EB","#36A2EB","#36A2EB","#36A2EB"]}]},options:{responsive:!0,legend:{display:!1},scales:{yAxes:[{barPercentage:.5,categoryPercentage:.5}]}}})}window.addEventListener("load",function(){"undefined"!=typeof web3?window.web3=new Web3(web3.currentProvider):(console.log("No web3? You should consider trying MetaMask!"),window.web3=new Web3(new Web3.providers.HttpProvider("http://localhost:8545"))),startApp()});